package holaMundo;

public class Char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char miCaracter = 'a';
		System.out.println(miCaracter);
		
		char varChar = '\u0061';
		System.out.println(varChar);
		
		char varCharDecimal = 33;
		System.out.println(varCharDecimal);
		
		char varCharSimbolo = '!';
		System.out.println(varCharSimbolo);
		
		var varChar1 = '\u0021';
		System.out.println(varChar1);
		
		var varCharDecimal1 = (char)33;
		System.out.println(varCharDecimal1);
		
		var varCharSimbolo1 = '!';
		System.out.println(varCharSimbolo1);
	}

}
